import React, { useState } from 'react';
import './login.css';
import loginImage from './assets/login-image-background.svg';
import Logo from './assets/tammina-blue-logo.svg';


// import loginImage from './login-bg.png'; // ðŸ‘ˆ your illustration image

const Login = ({ onLoginSuccess }) => {
  const [email, setEmail] = useState('');
  const [Password, setPassword] = useState('');


  const handleSubmit = (e) => {
    e.preventDefault();
    if (email.trim() !== '') {
      onLoginSuccess();
    }
  };

  return (
    <div className="login-container">
      {/* Left side illustration */}
      <div className="login-left">
        <img src={loginImage} alt="Tech Tammina illustration" />
      </div>

      {/* Right side form */}
      <div className="login-right">
        <div className="login-box">
          <img
            src={Logo}
            alt="Tech Tammina Logo"
            className="logo"
          />
          <h2>Log into your account</h2>

          <form onSubmit={handleSubmit}>
            <label>Email</label>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
                <label>Password</label>

                <input
              type="Password"
              placeholder="Enter your email"
              value={Password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />

            <div className="forgot-password">
              <a href="#">Forgot Password?</a>
            </div>

            <button type="submit">NEXT</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
