import React, { useState } from 'react';
import './login.css';

// import loginImage from './login-bg.png'; // ðŸ‘ˆ your illustration image

const Login = ({ onLoginSuccess }) => {
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (email.trim() !== '') {
      onLoginSuccess();
    }
  };

  return (
    <div className="login-container">
      {/* Left side illustration */}
      <div className="login-left">
        <img src="../assets/login-bg.png" alt="Tech Tammina illustration" />
      </div>

      {/* Right side form */}
      <div className="login-right">
        <div className="login-box">
          <img
            src="https://www.techtammina.com/wp-content/uploads/2020/03/TechTammina_Logo.png"
            alt="Tech Tammina Logo"
            className="logo"
          />
          <h2>Log into your account</h2>

          <form onSubmit={handleSubmit}>
            <label>Email</label>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />

            <div className="forgot-password">
              <a href="#">Forgot Password?</a>
            </div>

            <button type="submit">NEXT</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
